import { Outlet, useNavigation } from 'react-router-dom';
import MainNavigation from './MainNavigation';

function RootLayout() {
  const navigation = useNavigation();

  navigation.state === 'submitting';

  return (
    <>
      <MainNavigation />
      <main>
        <Outlet />
      </main>
    </>
  );
}

export default RootLayout;
